mapper.select:

  	INSERT INTO device_info
		(
		    id
		 , user_agent
		, os
		, browser
		, device
		, os_version
		, device_type
		, browser_version
		, orientation
		, create_by
		, create_date
		, modify_date
		, ip
		)
	VALUES(
	    #{id}
		, #{userAgent}
		, #{os}
		, #{browser}
		, #{device}
		, #{osVersion}
		, #{deviceType}
		, #{browserVersion}
		, #{orientation}
		, #{userID}
		, #{date}
		, #{date}
		, #{networkIP}
		);

mapper.update:

            UPDATE device_info
			SET
			     os            = #{os}
			    , browser       = #{browser}
			    , device        = #{device}
			    , os_version    = #{osVersion}
			    , device_type        = #{deviceType}
			    , browser_version    = #{browserVersion}
			    , orientation        = #{orientation}
			    , modify_date        = #{date}
			    , ip                 = #{networkIP}
			WHERE create_by = #{userID} and user_agent = #{userAgent};
